source i1
{
	type = mysql


	sql_query = select id, UNCOMPRESS(content) body, title, channel_id idd, ts, published, url_crc, author_id from posting
	sql_attr_uint		= idd
	sql_attr_uint		= ts
	sql_attr_uint		= published
	sql_attr_uint		= url_crc
	sql_attr_uint		= author_id
}

