source lj
{
	type = mysql


	sql_query = select id, \
		title, uncompress(content) content, \
		unix_timestamp(published) published, channel_id \
		from posting \
		where id <= 1000000;

	sql_attr_uint = channel_id
	sql_attr_timestamp = published
}

index lj
{
	source	= lj
	path	= data/lj
}
