test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{

}

source srctest
{
	type			= mysql


	sql_query		= SELECT * FROM test_table
}

index test
{
	source			= srctest
	path			= /test
	wordforms		= wordforms.txt
}

source src_num
{
	type			= mysql


	sql_query		= SELECT 1 id, 11 idd, 's01e01 stuff' text UNION SELECT 2 id, 11 idd, 'season 1 episode 1 stuff' text UNION SELECT 3 id, 11 idd, 's02e02' text UNION SELECT 4 id, 11 idd, 's3 e3' text
	sql_attr_uint = idd
}

index test_num
{
	source			= src_num
	path			= /test_num
	wordforms		= /wf_num.txt
}

source src_multi
{
	type			= mysql


	sql_query		= SELECT 1 id, 11 idd, 'test me rd' text UNION SELECT 2 id, 11 idd, 'test me red dogs' text UNION SELECT 3 id, 11 idd, 'test me red dog' text
	sql_attr_uint = idd
}

index multi1
{
	source			= src_multi
	path			= /multi1
	wordforms		= /wf_multi.txt
}

index multi2
{
	source			= src_multi
	path			= /multi2
	wordforms		= /wf_multi.txt
	morphology		= stem_en
	index_exact_words = 1
}
