test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{



	dist_threads = 2



}

source srctest
{
	type			= mysql

	sql_query		= SELECT * FROM test_table
	sql_attr_uint	= gid
	sql_attr_float	= price
	sql_attr_uint	= views
}

source srctest2 : srctest
{
	sql_query		= SELECT document_id+10, body, gid, price, views FROM test_table
}

source srctest3 : srctest
{
	sql_query		= SELECT document_id+20, body, gid, price*2 price, views FROM test_table
}


index test
{
	source			= srctest
	path			= /test
	docinfo			= extern
}

index test3
{
	source			= srctest3
	path			= /test3
	docinfo			= extern
}

index dist
{
	type			= distributed
	local			= test
	agent			= :test2
}

index dist2
{
	type			= distributed
	agent			= :test2
	agent			= :testr
}
index dist3
{
	type			= distributed
	local			= test
	agent			= :test2,test4
}


source src10
{
	type			= mysql

	sql_query = SELECT 1 id, 11 idd, 'aab cd' as text
	sql_attr_uint	= idd
}

index i10
{
	source			= src10
	path			= /i10
	docinfo			= extern
	dict = keywords
	min_prefix_len = 1
}
index i11
{
	source			= src10
	path			= /i11
	docinfo			= extern
	dict = keywords
	min_prefix_len = 1
}
index i12
{
	source			= src10
	path			= /i12
	docinfo			= extern
	charset_table	= b..z
	dict = keywords
	min_prefix_len = 1
}



index test2
{
	source			= srctest2
	path			= /test2
	docinfo			= extern
}
index test4 : test2
{
	path			= /test4
}


index testr
{
	source			= srctest
	path			= /testr
	docinfo			= extern
}
