}

source src_tsv1
{
	type = tsvpipe
	tsvpipe_command = cat /data1.tsv
	tsvpipe_field = title
	tsvpipe_field = content
	tsvpipe_attr_multi = tags
	tsvpipe_attr_uint = gid:3
}

index tsv1
{
	source = src_tsv1
	path = /tsv1
}

