test.config:
indexer
{
	mem_limit = 16M
}

searchd
{

}

source main
{
	type = mysql

	sql_query = select 1 as id, 'phantom' as body, 2 as attr;
	sql_attr_uint = attr
}

source delta
{
	type = mysql

	sql_query = SELECT * FROM test_table;
	sql_attr_uint = attr
}

index main
{
	source			= main
	path			= /empty
}

index delta
{
	source			= delta
	path			= /delta
}

test.db_create:
CREATE TABLE test_table
(
	`id` int(11) NOT NULL default '0',
	`body` varchar(255) NOT NULL default '',
	`attr` int(11) NOT NULL default '0'
);
