test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{

	workers = threads
}

source srctest
{
	type			= mysql


	sql_query		= SELECT * FROM test_table
}

index test
{
	source			= srctest
	path			= /test
	morphology		= stem_en
	index_exact_words	= 1
	expand_keywords	= 1
}

source src_prefix
{
	type			= mysql


	sql_query		= SELECT 1, 'force all' UNION SELECT 2, 'forcing me' UNION SELECT 3, 'foam stuff'
}
index prefix
{
	source			= src_prefix
	path			= /prefix
	expand_keywords	= 1
	min_prefix_len = 2
}

index expanded_plain
{
	source			= srctest
	path			= /expanded-plain
	docinfo = extern
	dict = keywords
	expand_keywords	= 1
	min_infix_len = 3
}

index expanded_rt
{
	type = rt
	path			= /expanded-rt
	dict = keywords
	expand_keywords	= 1
	min_infix_len = 3
	rt_field = title
	rt_attr_uint = idd	
}


source src_empty
{
        type                    = mysql

        sql_query               = SELECT * FROM empty_table
        sql_attr_uint           = uid
}
index empty
{
        type                    = plain
        path                    = /empty_kw
        source                  = src_empty
        min_infix_len           = 3
        dict                    = keywords
}


source src_inline
{
	type			= mysql


	sql_query		= SELECT *, document_id+100 as idd FROM test_table
	sql_attr_uint = idd
}

index plain_inline
{
	source			= src_inline
	path			= /plain-inline
	docinfo = inline
	dict = keywords
	min_prefix_len = 1
}
