test.config:
indexer { mem_limit = 16M }
searchd {
expansion_limit = 0 expansion_limit = 3 expansion_limit = 2
workers = threads }
source test { type = mysql
sql_query = select * from test_table sql_attr_uint = gid }
index test { source = test path = /test
dict = keywords min_prefix_len = 2 }
index rt { type = rt path = /rt docinfo = extern rt_field = data rt_attr_uint = idd dict = keywords min_prefix_len = 1 }
source plain_prefix { type = mysql
sql_query_pre = SET NAMES utf8 sql_query = select 1000 as id, 1 as gid, 'Москва родимая' as text sql_attr_uint = gid } index plain_prefix { source = plain_prefix path = /plain_prefix
dict = keywords min_prefix_len = 1 } index rt_prefix { type = rt path = /rt_prefix docinfo = extern rt_field = data rt_attr_uint = idd dict = keywords min_prefix_len = 1 }
source plain_substring { type = mysql
sql_query = select 1 id, 1 gid, '12345' as text sql_attr_uint = gid } index plain_sub_prefix { source = plain_substring path = /plain_sub_prefix
min_prefix_len = 2 max_substring_len = 3 dict = crc } index plain_sub_infix { source = plain_substring path = /plain_sub_infix
min_infix_len = 2 max_substring_len = 3 dict = crc }
source kw_infix { type = mysql
sql_query = SELECT 1, '1' title, 100 as idd sql_attr_uint = idd }
index kw_infix { source = kw_infix path = /kw_infix docinfo = extern min_infix_len = 3 dict = keywords }
source kw_infix_morph { type = mysql
sql_query = SELECT 1, 'running star' title, 100 as idd UNION SELECT 2, 'run off', 100 as idd sql_attr_uint = idd }
index kw_infix_morph { source = kw_infix_morph path = /kw_infix_morph docinfo = extern min_infix_len = 1 dict = keywords morphology = stem_en }
source plain_prefix_limit { type = mysql
sql_query_pre = SET NAMES utf8 sql_query = select 1000 as id, 1 as gid, 'hello two world' as text sql_attr_uint = gid } index plain_prefix_limit { source = plain_prefix_limit path = /plain_prefix_limit dict = keywords min_prefix_len = 4 } index rt_prefix_limit { type = rt path = /rt_prefix_limit docinfo = extern rt_field = data rt_attr_uint = idd dict = keywords min_prefix_len = 4 }

test.db_create:
create table test_table
(
	id int not null,
	gid int not null,
	title varchar(255) not null
);
