test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{

}

source test
{
	type			= mysql

	sql_query		= SELECT * FROM test_table
}

index test
{
	source	= test
	path	= /test

	blend_chars = @, &, ., !, -


		blend_mode = trim_none
		blend_mode = trim_none, skip_pure
		blend_mode = trim_tail
		blend_mode = trim_head, trim_tail

}

test.db_create:
CREATE TABLE test_table
(
	id INT NOT NULL,
	text VARCHAR(255) NOT NULL
);
