test.config:
indexer { mem_limit = 16M }
searchd {
snippets_file_prefix = / }
source src { type = mysql
sql_query = SELECT 1, 'dummy', 1 as idd sql_attr_uint = idd }
index idx { source = src docinfo = extern path = /main blend_chars = . }
index idx2 { source = src docinfo = extern path = /idx2 morphology = stem_en charset_table = 0..9, a..z, A..Z->a..z blend_chars = U+0028, U+0029, U+002f, U+002b blend_mode = trim_none, trim_head, trim_tail, trim_both, skip_pure }
index idx_blend1 { type =template html_strip = 1 min_word_len = 2 min_infix_len = 3 index_sp = 1 dict = keywords morphology = stem_en,stem_ru blend_chars = . blend_mode = trim_both }
index idx_blend2 { type = template min_word_len = 2 charset_type = utf-8 charset_table = 0..9, A..Z->a..z, _, a..z, U+410..U+42F->U+430..U+44F, U+430..U+44F, U+0401->U+0435, U+0451->U+0435, U+A8->U+E5, U+B8->U+E5 ignore_chars = U+AD min_prefix_len = 2 blend_chars = +, U+23 html_strip = 1 index_exact_words = 1 morphology = stem_ru }

