test.config:
indexer { mem_limit = 16M }
searchd {
workers = threads }
source disk { type = mysql
sql_query = select * from test_table sql_attr_uint = gid }
index disk { source = disk path = /disk charset_table = a..z, A..Z->a..z html_strip = 1 html_remove_elements = stuff }
index rt { type = rt path = data/rt rt_attr_uint = gid1 rt_attr_uint = gid2 rt_field = title rt_field = content charset_table = a..z, A..Z->a..z, _ }
source src_plain { type = mysql
sql_query = select id, title, 'dummy for' as str1, '55, 15, 20' as mva1, '' as mva2, gid from test_table sql_attr_string = str1 sql_attr_multi = uint mva1 from field sql_attr_multi = uint mva2 from field sql_attr_uint = gid }
index plain { source = src_plain path = /plain }
index rt_arena { type = rt path = data/rt_arena
rt_field = title rt_attr_string = str1 rt_attr_multi = mva1 rt_attr_uint = gid
}
source src_a1 { type = mysql
sql_query = select id, title, 'dummy' as body, gid from test_table sql_attr_uint = gid }
index a1 { source = src_a1 path = /a1 }
index rt1 { type = rt path = data/rt1 rt_field = title rt_field = body rt_attr_uint = gid }

test.db_create:
CREATE TABLE test_table
(
	id INTEGER PRIMARY KEY NOT NULL,
	gid INTEGER NOT NULL,
	title VARCHAR(255) NOT NULL
);
