test.config:
indexer
{
	mem_limit			= 16M
}

searchd
{

	workers = threads
}

source test
{
	type				= mysql

	sql_query_pre		= set names utf8
	sql_query			= select * from test_table
	sql_file_field 		= file_field
}

source test_xml
{
	type = xmlpipe2
	xmlpipe_command = cat /data.xml
}

index base
{
	source			= test
	path			= /base

	charset_table	= 0..9, A..Z->a..z, _, a..z, U+410..U+42F->U+430..U+44F, U+430..U+44F, \
			U+2E80..U+2EF3, U+2F00..U+2FD5, U+3105..U+312D, U+31C0..U+31E3, U+3400..U+4DB5, U+4E00..U+9FCC, \
			U+F900..U+FAD9, U+20000..U+2FA1D


		morphology 		= rlp_chinese, stem_enru
		morphology 		= rlp_chinese_batched, stem_enru


	blend_chars		= -
	rlp_context		= ../rlp/samples/etc/rlp-chinese-context.xml
}

index test : base
{
	source			= test
	path			= /test
	wordforms		= /wordforms.txt
}

index test2 : base
{
	type			= rt
	path			= /test2
	rt_field		= title
	rt_attr_uint	= tag
}

index test_stripped : base
{
	source			= test
	path			= /test_stripped
	html_strip		= 1
}

index test_xml : base
{
	source			= test_xml
	path			= /test_xml
	wordforms		= /wordforms.txt
}

source test_spec
{
	type				= mysql

	sql_query_pre		= set names utf8
	sql_query			= select 100 id, 'the 凤1 a' title, 11 as idd UNION select 200 id, '凤1' title, 11 as idd UNION select 300 id, '凤1 轉注转注' title, 11 as idd UNION select 400 id, '凤 注 轉凤'  title, 11 as idd  

	sql_field_string = title
    sql_attr_uint = idd
}

