test.config:
indexer
{
	mem_limit = 16M
}

searchd
{

}

source pure
{
	type					= mysql


	sql_query				= select id, idd from test;
	sql_attr_uint			= idd
	sql_joined_field		= text from query; select id, text from test_joined;
}

index pure
{
	source	= pure
	path	= /pure

    charset_type			= utf-8
    docinfo					= extern
}

source test
{
	type					= mysql


	sql_query				= select id, text, idd from test;
	sql_joined_field		= text from query; select id, text from test_joined;
	sql_attr_uint			= idd
}

index test
{
	source	= test
	path	= /test

    charset_type			= utf-8
    docinfo					= extern

    phrase_boundary			= .
    phrase_boundary_step	= 2
	stopwords				= /stopwords.txt
}

source ignore_absent_joined_ids
{
	type = mysql


	sql_query = SELECT 1, 'one two three'
	sql_joined_field = test from query; SELECT 2, 'пыля безопастн'

}

index ignore_absent_joined_ids
{
	source = ignore_absent_joined_ids
	path = /ignore_absent_joined_ids
}

test.db_create:
create table test
(
	id int,
	text varchar(255),
	idd int
);

create table test_joined
(
	id int,
	text varchar(255)
);
